package com.coreds.adhoc;

import java.util.HashMap;
import java.util.Map;

public class IsPermutationSubstring 
{
	public static void main (String[] args)	{
		checkInclusion("abcd", "badc");
		checkInclusion("abcd", "dadz");
		checkInclusion("ab", "eidbaooo");
		checkInclusion("", 
				"");
	}
	
	private static boolean checkInclusion (String s1, String s2)	{
		System.out.println("checkInclusion("+s1+","+s2+")");
		boolean isPermIncluded = false;
		System.out.println("    s1len="+s1.length()+", s2len="+s2.length());
		if (s1.length() <= s2.length()) {
			Map<Character,Integer> map1 = parseString(s1);
			System.out.println("    s1Map="+map1.toString());
			for (int i = 0; i+s1.length() <= s2.length(); i++)	{
				String s3 = s2.substring(i, i+s1.length());
				Map<Character,Integer> map2 = parseString(s3);
//				System.out.println("    s3="+s3);
	//			System.out.println("    s2map="+map2.toString());
				if (areMapsSame(map1, map2))	{	
					isPermIncluded = true;
					break;
				}
			}
		}
		System.out.println("checkInclusion = "+isPermIncluded);
		return isPermIncluded;
	}
	
	private static Map<Character,Integer> parseString (String s){
		Map<Character,Integer> map = new HashMap<Character,Integer>();
		for (int i = 0; i < s.length(); i++)	{
			Integer count = map.get(s.charAt(i));
			if (count == null) count = 1;
			else count++;
			map.put(s.charAt(i), count);
		}
		//System.out.println("parseString, "+s+", "+map.toString());
		return map;
	}
	
	private static boolean areMapsSame (Map<Character,Integer> map1, Map<Character,Integer> map2) {
		if (map1.keySet().size() != map2.keySet().size()) return false;
		for (Character c1 : map1.keySet())	{
			Integer count1 = map1.get(c1);
			Integer count2 = map2.get(c1);
			if (count1 != count2) return false;
		}
		return true;
	}
}
